CREATE PROCEDURE [pangya].[ProcUpdateClubSetWorkshop] 
	-- Add the parameters for the stored procedure here
	@IDUSER INT,
	@ITEM_ID INT,
	@LEVEL INT,
	@C0 SMALLINT,
	@C1 SMALLINT,
	@C2 SMALLINT,
	@C3 SMALLINT,
	@C4 SMALLINT,
	@MASTERY INT,
	@RANK INT,
	@RECOVERY INT,
	@FLAG INT
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT ON;

    IF @IDUSER > 0 AND @ITEM_ID > 0
	BEGIN
		
		IF @FLAG = 4	-- UP RANK CLUBSET tira Mastery e add aos mastery GASTO
		BEGIN
			UPDATE pangya.pangya_item_warehouse
			SET Mastery_Gasto = Mastery_Gasto + (Mastery_Pts - @MASTERY)	-- Aqui tem que add, diminui o Mastery, mas tem que aumentar no Mastery Gasto
			WHERE [UID] = @IDUSER AND item_id = @ITEM_ID
		END
		ELSE IF @FLAG = 0	-- TRANSFER MASTERY POINTS
		BEGIN
			UPDATE pangya.pangya_item_warehouse
			SET Total_Mastery_Pts = Total_Mastery_Pts + (@MASTERY - Mastery_Pts) -- ATT o TOTAL de Mastery Pts
			WHERE [UID] = @IDUSER AND item_id = @ITEM_ID
		END

		UPDATE pangya.pangya_item_warehouse 
		SET ClubSet_WorkShop_C0 = @C0,
			ClubSet_WorkShop_C1 = @C1,
			ClubSet_WorkShop_C2 = @C2,
			ClubSet_WorkShop_C3 = @C3,
			ClubSet_WorkShop_C4 = @C4,
			[Level] = @LEVEL,
			Mastery_Pts = @MASTERY,
			Up = @RANK,	-- UP é o RANK
			Recovery_Pts = @RECOVERY
		WHERE [UID] = @IDUSER AND item_id = @ITEM_ID
	END
END
