CREATE PROCEDURE [pangya].[ProcUpdateGuildNewMarkIdx]
	-- Add the parameters for the stored procedure here
	@GUILD_UID INT,
	@MASTER INT,
	@NEW_MARK_IDX INT
AS
BEGIN

	DECLARE @RET INT = -1
	
	IF @GUILD_UID > 0 AND @NEW_MARK_IDX > 0 AND @MASTER > 0
	BEGIN

		IF (SELECT GUILD_UID FROM pangya.pangya_guild WHERE [GUILD_UID] = @GUILD_UID AND GUILD_LEADER = @MASTER) IS NOT NULL
		BEGIN

			DECLARE @PANG BIGINT = (SELECT pang FROM pangya.user_info WHERE [UID] = @MASTER)

			-- Verifica se ele tem os 5k de pangs para fazer o pedido para trocar de guild mark
			IF @PANG IS NOT NULL AND @PANG >= 5000
			BEGIN

				UPDATE
					pangya.pangya_guild
						SET GUILD_NEW_MARK_IDX = @NEW_MARK_IDX
				WHERE [GUILD_UID] = @GUILD_UID

				-- Tira os 5k de pangs é o valor para trocar o guild mark
				UPDATE
					pangya.user_info
						SET Pang = @PANG - 5000
				WHERE [UID] = @MASTER

				-- Sucesso
				SET @RET = 1

			END
			ELSE
				SET @RET = -2	-- Não tem pangs suficiente para trocar o guild mark

		END
		ELSE
			SET @RET = -3	-- Player não é o master da Guild

	END

	SELECT @RET AS RET
END
