CREATE PROCEDURE [pangya].[ProcUpdateUCC]
	-- Add the parameters for the stored procedure here
	@IDUSER INT,
	@UCC_ID INT,
	@UCC_IDX NVARCHAR(8),
	@UCC_NAME NVARCHAR(40),
	@DT_DRAW datetime2,
	@COPIER_NICK NVARCHAR(22),
	@COPIER INT,
	@STATUS TINYINT,
	@USE_YN NVARCHAR(1),
	@TYPE TINYINT
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	--#Salva SelfDesign UCC
	--DECLARE @ITEMID INT;
	
	--SET @ITEMID = 0;

	--SELECT ITEM_ID INTO ITEMID 
	--FROM tu_ucc
	--WHERE 
	--	UID = @IDUSER
	--	AND TYPEID = @TYPEID
	--	AND UCCIDX = @UCC_IDX
	--	AND USE_YN IN ('N', 'T')
	--	AND UCC_NAME IS NULL
	
	
	IF @IDUSER > 0 AND @UCC_ID > 0
	BEGIN
		IF @TYPE = 2/*COPY UCC*/
		BEGIN
			DECLARE @SEQ INT = ISNULL((SELECT COUNT_BIG(seq) as COUNT FROM pangya.tu_ucc WHERE UCCIDX = @UCC_IDX), 0)
			
			SET @SEQ = CASE WHEN @SEQ = 0 THEN 1 ELSE @SEQ + 1 END

			UPDATE pangya.tu_ucc
			SET USE_YN = @USE_YN
				 ,UCC_NAME = @UCC_NAME
				 ,DRAW_DT = @DT_DRAW
				 ,UCCIDX = @UCC_IDX
				 ,COPIER_NICK = @COPIER_NICK
				 ,COPIER = @COPIER
				 ,[STATUS] = @STATUS
				 ,SEQ = @SEQ
			WHERE
				UID = @IDUSER
				AND item_id = @UCC_ID

			SELECT @SEQ AS SEQ
		END
		ELSE /*SAVE TEMPORARIO E DEFINITIVO*/
		BEGIN
			UPDATE pangya.tu_ucc
			SET USE_YN = @USE_YN
				 ,UCC_NAME = @UCC_NAME
				 ,DRAW_DT = @DT_DRAW
				 ,UCCIDX = @UCC_IDX
				 ,COPIER_NICK = @COPIER_NICK
				 ,COPIER = @COPIER
				 ,[STATUS] = @STATUS
			WHERE
				UID = @IDUSER
				AND item_id = @UCC_ID
	
			SELECT @UCC_ID AS ITEM_ID
		END
	END
	ELSE
		SELECT @UCC_ID AS ITEM_ID
END
