CREATE PROCEDURE [pangya].[USP_ADD_ITEM]
	-- Add the parameters for the stored procedure here
	@IDUSER INT,
	@GIFTFLAG INT,
	@PURCHASE_IN TINYINT,
	@IDITEM int,
	@ITEMTYPEID INT,
	@TYPEFLAG TINYINT,
	@TYPEFLAG_IFF TINYINT,
	@TEMPO INT,
	@C_0 smallint,
	@C_1 smallint,
	@C_2 smallint,
	@C_3 smallint,
	@C_4 smallint,
	@X float,
	@Y float,
	@Z float,
	@R float
AS
BEGIN
	DECLARE   @NUMERO INT
		 ,@CONTADOR INT
		 ,@CHARACTER INT 
		 ,@PERIODO INT
		 ,@CADDIE_TYPE INT
		 ,@ITEM_TYPE INT 
		 ,@COR_CABELO INT
		 ,@Gift_flg INT

	SET @NUMERO = dbo.GroupID(@ITEMTYPEID)

	IF @NUMERO = 4  --#Character
		BEGIN
			IF @IDITEM <= 0 BEGIN
				SET @IDITEM = (SELECT item_id
				FROM pangya.pangya_character_information 
				WHERE UID = @IDUSER AND typeid = @ITEMTYPEID)
			END

			IF @IDITEM > 0
				BEGIN
					UPDATE pangya_user_equip SET character_id = @IDITEM WHERE UID = @IDUSER;
				END;
			ELSE
				BEGIN
					exec FuncInsertPartsCharacter @IDUSER, @ITEMTYPEID, 0, 0

					SET @IDITEM = (SELECT MAX(item_id)FROM pangya_character_information WHERE UID = @IDUSER AND typeid = @ITEMTYPEID)

					SET @CONTADOR = 0;
					WHILE @CONTADOR < 24
						INSERT INTO td_char_equip_s4(UID, CHAR_ITEMID, IN_DATE, EQUIP_NUM, USE_YN)
								VALUES(@IDUSER, @IDITEM, getdate(), @CONTADOR + 1, 'Y');
						SET @CONTADOR = @CONTADOR + 1;

					UPDATE pangya_user_equip SET character_id = @IDITEM WHERE UID = @IDUSER;
				END

			SELECT @IDITEM AS ITEM_ID;
		END;
	ELSE IF @NUMERO = 8 --#Parts
		BEGIN

			IF @TYPEFLAG_IFF = 8 OR @TYPEFLAG_IFF = 9
			BEGIN
				IF @IDITEM <= 0
					exec pangya.USP_ADD_UCC @IDUSER, @ITEMTYPEID, @GIFTFLAG, @C_0, @C_1, @C_2, @C_3, @C_4, @PURCHASE_IN, @TYPEFLAG_IFF, @TEMPO
				ELSE IF @IDITEM > 0 AND @C_0 >= 0
				BEGIN
					UPDATE pangya.pangya_item_warehouse SET valid = 1, Gift_flag = 0 
					WHERE UID = @IDUSER AND item_id = @IDITEM;

					SELECT @IDITEM AS ITEM_ID, 
							(SELECT UCCIDX FROM tu_ucc WHERE UID = @IDUSER AND ITEM_ID = @IDITEM) AS UCCIDX,
							(SELECT SEQ FROM tu_ucc WHERE UID = @IDUSER AND ITEM_ID = @IDITEM) AS SD_SEQ;
				END
				ELSE IF @IDITEM > 0 AND @C_0 < 0
				BEGIN
					UPDATE pangya.pangya_item_warehouse SET valid = 0
					WHERE UID = @IDUSER AND item_id = @IDITEM;

					SELECT @IDITEM AS ITEM_ID, 
							(SELECT UCCIDX FROM tu_ucc WHERE UID = @IDUSER AND ITEM_ID = @IDITEM) AS UCCIDX,
							(SELECT SEQ FROM tu_ucc WHERE UID = @IDUSER AND ITEM_ID = @IDITEM) AS SD_SEQ;
				END
			END
			ELSE
			BEGIN
				IF @IDITEM > 0 --#Extender o Rental
					BEGIN
						IF @TEMPO > 0 AND (SELECT TOP 1 flag FROM pangya.pangya_item_warehouse WHERE UID = @IDUSER AND typeid = @ITEMTYPEID) = 96
							UPDATE pangya.pangya_item_warehouse SET EndDate = DATEADD(DAY, @TEMPO, getdate()) 
							WHERE UID = @IDUSER AND item_id = @IDITEM;
						ELSE IF @C_0 < 0
							UPDATE pangya.pangya_item_warehouse SET valid = 0 
							WHERE UID = @IDUSER AND item_id = @IDITEM;
						ELSE IF @C_0 > 0
							UPDATE pangya.pangya_item_warehouse SET valid = 1, Gift_flag = @GIFTFLAG 
							WHERE UID = @IDUSER AND item_id = @IDITEM;
					END;
				ELSE
					BEGIN						
						IF @TEMPO > 0
						BEGIN
							INSERT INTO pangya.pangya_item_warehouse(UID, typeid, valid, regdate, Gift_flag, flag, Purchase, ItemType, EndDate, c0, c1, c2, c3, c4)
										VALUES(@IDUSER, @ITEMTYPEID, 1, getdate(), @GIFTFLAG, @TYPEFLAG, 0, 2, DATEADD(DAY, @TEMPO, getdate()), @C_0, @C_1, @C_2, @C_3, @C_4);
						END;
						ELSE
						BEGIN
							INSERT INTO pangya.pangya_item_warehouse(UID, typeid, valid, regdate, enddate, Gift_flag, flag, Purchase, ItemType, c0, c1, c2, c3, c4)
										VALUES(@IDUSER, @ITEMTYPEID, 1, getdate(), getdate(), @GIFTFLAG, @TYPEFLAG, 0, 2, @C_0, @C_1, @C_2, @C_3, @C_4);
						END;

						SET @IDITEM = (SELECT MAX(ITEM_ID)FROM pangya_item_warehouse WHERE UID = @IDUSER)
					END

				SELECT @IDITEM AS ITEM_ID;
			END
		END;
	ELSE IF @NUMERO = 16 --#ClubSet
		BEGIN
			IF @IDITEM <= 0
			BEGIN
