CREATE PROCEDURE [pangya].[USP_CreateAchievementUser]
	-- Add the parameters for the stored procedure here
	@IDUSER INT
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT ON;

	INSERT INTO ACHIEVEMENT_TIPO(UID, NOME, TYPEID, TIPO, [OPTION]) SELECT @IDUSER, nome, typeid, tipo, [option] FROM achievements group by typeid, nome, typeid, tipo, [option], [index] ORDER BY [index];

	--#INSERT INTO COUNT_ITEM(UID, NOME, TYPEID, TIPO) SELECT IDUSER, nome, typeid, 1 as tipo_ FROM counter_items;

	INSERT INTO ACHIEVEMENT_QUEST(UID, ID_ACHIEVEMENT, TYPEID_ACHIEVE, COUNT_ID, OBJETIVO_QUEST) SELECT @IDUSER, x.ID_ACHIEVEMENT, x.typeid_d, 0, x.objetivo_quest FROM
	(
		SELECT e.* FROM
			(SELECT c.*, d.* FROM
				(SELECT a.*, b.* FROM (SELECT * FROM ACHIEVEMENT_TIPO) a
				INNER JOIN
				(SELECT typeid as typeid_b, quest_typeid, [index] as index_b FROM achievements) b
				ON a.typeid = b.typeid_b) as c
			INNER JOIN
			(SELECT typeid as typeid_d, counter_typeid, counter_qntd as Objetivo_Quest FROM quest_stuffs) d
			ON c.quest_typeid = d.typeid_d) e
            WHERE e.UID = @IDUSER
	)x  ORDER BY x.index_b;
    
    INSERT INTO COUNT_ITEM(UID, NOME, ID_ACHIEVEMENT, TYPEID, TIPO) SELECT @IDUSER, K.NOME, G.ID_ACHIEVEMENT, K.TYPEID, 1 FROM
		(SELECT J.*, H.* FROM
		(SELECT TOP 9223372036854775807 @IDUSER as [UID], x.ID_ACHIEVEMENT, x.typeid_d, 0 as column4, x.objetivo_quest FROM
			(
				SELECT e.* FROM
					(SELECT c.*, d.* FROM
						(SELECT a.*, b.* FROM (SELECT * FROM ACHIEVEMENT_TIPO) a
						INNER JOIN
						(SELECT typeid as typeid_b, quest_typeid, [index] as index_b FROM achievements) b
						ON a.typeid = b.typeid_b) c
					INNER JOIN
					(SELECT typeid as typeid_d, counter_typeid, counter_qntd as Objetivo_Quest FROM quest_stuffs) d
					ON c.quest_typeid = d.typeid_d) e
					WHERE e.UID = @IDUSER
			)x 
			GROUP BY x.ID_ACHIEVEMENT, x.typeid_d, x.Objetivo_Quest, x.index_b ORDER BY x.index_b) J
		INNER JOIN
        (SELECT * FROM quest_stuffs) H
        ON J.typeid_d = H.typeid) G
        INNER JOIN
        (SELECT * FROM counter_items) K
	ON K.typeid = G.counter_typeid;
    
    UPDATE a
		SET a.count_id = b.count_id
	FROM pangya.achievement_quest a
	INNER JOIN (SELECT * FROM pangya.count_item) b
	ON a.uid = @IDUSER AND a.ID_ACHIEVEMENT = b.ID_ACHIEVEMENT;
END
