CREATE PROCEDURE [pangya].[USP_EXP_CADDIE_ATT] 
	-- Add the parameters for the stored procedure here
	@IDUSER int,
	@caddie_id int,
	@xp int
AS
BEGIN
	DECLARE @cEXP_LVL INT = 0;
	DECLARE @cLvl INT,
			@exp INT

	SET @cLvl = (SELECT cLevel FROM pangya_caddie_information WHERE UID = @IDUSER AND item_id = @caddie_id);
    
    IF @cLvl < 3
	BEGIN
		SET @exp = (SELECT [Exp] FROM pangya_caddie_information WHERE UID = @IDUSER AND item_id = @caddie_id) + @xp;
		
		WHILE 1 = 1
		BEGIN
			SET @cEXP_LVL = 520 + (160 * @cLvl);
			
			IF @exp >= @cEXP_LVL
			BEGIN
				SET @cLvl = @cLvl + 1;
                
                IF @cLvl = 3
				BEGIN
					UPDATE pangya_caddie_information SET [Exp] = 0, cLevel = @cLvl
					WHERE UID = @IDUSER AND item_id = @caddie_id;
                    BREAK
				END
                ELSE
				BEGIN
					SET @exp = @exp - @cEXP_LVL;
					
					UPDATE pangya_caddie_information SET [Exp] = @exp, cLevel = @cLvl
					WHERE UID = @IDUSER AND item_id = @caddie_id;
                END
			END
			ELSE
			BEGIN
				UPDATE pangya_caddie_information SET [Exp] = @exp WHERE UID = @IDUSER AND item_id = @caddie_id;
                BREAK
			END
		END
	END

END
