CREATE PROCEDURE [pangya].[USP_EXP_PLAYER_ATT] 
	-- Add the parameters for the stored procedure here
	@IDUSER int,
	@EXP int,
	@OPT_IN int
AS
BEGIN
	--#ATT EXP Player
	DECLARE @OPT INT = 0;
	DECLARE @caddie_id INT,
			@mascot_id INT,
			@EXP_1 INT,
			@EXP_2 INT
    
    IF @OPT_IN = 0 --# add o EXP para o caddie e mascot tbm se for 0 a opção
	BEGIN
		--#ATT EXP DO CADDIE TBM
		SET @caddie_id = (SELECT caddie_id FROM pangya_user_equip WHERE UID = @IDUSER);
		
		IF @caddie_id != 0 AND @caddie_id IS NOT NULL
		BEGIN
			exec USP_EXP_CADDIE_ATT @IDUSER, @caddie_id, @EXP
		END
        
        SET @mascot_id = (SELECT mascot_id FROM pangya_user_equip WHERE UID = @IDUSER);
        
        IF @mascot_id != 0 AND @mascot_id IS NOT NULL
		BEGIN
			exec USP_EXP_MASCOT_ATT @IDUSER, @mascot_id, @EXP
        END
    END

	SET @EXP_1 = (SELECT xp FROM user_info WHERE UID = @IDUSER) + @EXP;

	WHILE 1 = 1
	BEGIN
		SET @EXP_2 = pangya.GetEXPByLevel(@IDUSER);

		IF @EXP_1 >= @EXP_2
		BEGIN
			SET @EXP_1 = @EXP_1 - @EXP_2;

			UPDATE user_info SET [level] = [level] + 1, xp = @EXP_1
			WHERE UID = @IDUSER;

			exec USP_LEVEL_REWARD @IDUSER

			SET @OPT = 1;
		END
		ELSE
		BEGIN
			UPDATE user_info SET xp = @EXP_1 WHERE UID = @IDUSER;
			BREAK
		END
	END

	SELECT @OPT as OPT;
END
