CREATE PROCEDURE [pangya].[USP_SAVE_ROOM]  
   @IDUSER int,
   @IDITEM int,
   @IDTYPE int,
   @NO_ROOM int,
   @X float(24),
   @Y float(24),
   @Z float(24),
   @R float(24),
   @YN_USE smallint
AS 
   BEGIN

      SET  XACT_ABORT  ON

      SET  NOCOUNT  ON

      DECLARE
         @Contador int, 
         @Contador2 int

      SELECT @Contador = count_big(*)
      FROM pangya.td_room_data
      WHERE td_room_data.UID = @IDUSER AND td_room_data.MYROOM_ID = @IDITEM

      SELECT @Contador2 = count_big(*)
      FROM pangya.td_room_data

      IF @Contador > 0
         UPDATE pangya.td_room_data
            SET 
               ROOM_NO = @NO_ROOM, 
               POS_X = @X, 
               POS_Y = @Y, 
               POS_Z = @Z, 
               POS_R = @R, 
               DISPLAY_YN = 
                  CASE 
                     WHEN @YN_USE = 1 THEN N'Y'
                     ELSE N'N'
                  END
         WHERE td_room_data.UID = @IDUSER AND td_room_data.MYROOM_ID = @IDITEM
      ELSE 
         IF @IDITEM IS NULL OR @IDITEM = 0
            BEGIN
               INSERT pangya.td_room_data(
                  pangya.td_room_data.UID, 
                  pangya.td_room_data.ROOM_NO, 
                  pangya.td_room_data.TYPEID, 
                  pangya.td_room_data.POS_X, 
                  pangya.td_room_data.POS_Y, 
                  pangya.td_room_data.POS_Z, 
                  pangya.td_room_data.POS_R, 
                  pangya.td_room_data.MOD_SEQ, 
                  pangya.td_room_data.DISPLAY_YN, 
                  pangya.td_room_data.MOD_DT)
                  VALUES (
                     @IDUSER, 
                     @NO_ROOM, 
                     @IDTYPE, 
                     @X, 
                     @Y, 
                     @Z, 
                     @R, 
                     @Contador2 + 1, 
                     N'Y', 
                     getdate())
            END
         ELSE 
            BEGIN

               SET  IDENTITY_INSERT pangya.td_room_data  ON

               INSERT pangya.td_room_data(
                  pangya.td_room_data.UID, 
                  pangya.td_room_data.ROOM_NO, 
                  pangya.td_room_data.TYPEID, 
                  pangya.td_room_data.MYROOM_ID, 
