-- =============================================
-- Author:		<Author,,Name>
-- Create date: <Create Date,,>
-- Description:	<Description,,>
-- =============================================
CREATE PROCEDURE [pangya].[USP_UPDATE_GUILD_RANKING]
AS
BEGIN

	DECLARE @GUID INT
	DECLARE @RANK INT = 1

	DECLARE tmp CURSOR FOR
		SELECT 
			GUILD_UID 
		FROM pangya.pangya_guild
		WHERE GUILD_POINT <> 0 AND GUILD_PANG <> 0 AND (GUILD_WIN <> 0 OR GUILD_LOSE <> 0 OR GUILD_DRAW <> 0)
			AND (GUILD_STATE NOT IN(4, 5) OR GUILD_CLOSURE_DATE IS NULL OR getdate() < GUILD_CLOSURE_DATE)
		ORDER BY GUILD_WIN DESC, GUILD_LOSE, GUILD_DRAW, GUILD_POINT DESC, GUILD_PANG DESC

	OPEN tmp
	FETCH NEXT FROM tmp INTO @GUID
	WHILE @@FETCH_STATUS = 0
	BEGIN

		IF (SELECT GUILD_UID FROM pangya.pangya_guild_ranking WHERE GUILD_UID = @GUID) IS NOT NULL
		BEGIN
		
			-- Já tem a Guild no Rank Atualiza
			UPDATE pangya.pangya_guild_ranking
				SET LAST_RANK = [RANK],
					[RANK] = @RANK,
					REG_DATE = getdate()
			WHERE GUILD_UID = @GUID

		END
		ELSE
		BEGIN

			-- Não tem a Guild no Rank Adiciona
			INSERT INTO pangya.pangya_guild_ranking(GUILD_UID, [RANK], LAST_RANK, REG_DATE)
			VALUES(@GUID, @RANK, -1/*LAST RANK*/, getdate())
		END

		-- SOMA RANK + 1
		SET @RANK = @RANK + 1

		-- FETCH NEXT
		FETCH NEXT FROM tmp INTO @GUID
	END

	CLOSE tmp
	DEALLOCATE tmp

END
